import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { CopyToClipboard } from 'react-copy-to-clipboard';
import chroma from 'chroma-js';
import { withStyles } from '@material-ui/styles';
import './ColorBox.css';

const styles = {
  copyText: {
    color: props => chroma(props.background).luminance() >= 0.68 ? "black" : "white"
  },
  colorName: {
    color: props => chroma(props.background).luminance() <= 0.19 ? "white" : "rgba(0,0,0,0.5)"
  },
  seeMore: {
    color: props => chroma(props.background).luminance() >= 0.68 ? "rgba(0,0,0,0.5)" : "white",
    background: "rgba(255, 255, 255, 0.3)",
    position: "absolute",
    border: "none",
    right: "0px",
    bottom: "0px",
    width: "60px",
    height: "30px",
    textAlign: "center",
    lineHeight: "30px",
    textTransform: "uppercase"
  },
  copyButton: {
    color: props => chroma(props.background).luminance() >= 0.68 ? "rgba(0,0,0,0.5)" : "white",
    width: "100px",
    height: "30px",
    position: "absolute",
    display: "inline-block",
    top: "50%",
    left: "50%",
    marginLeft: "-50px",
    marginTop: "-15px",
    textAlign: "center",
    outline: "none",
    background: "rgba(255, 255, 255, 0.3)",
    fontSize: "1rem",
    lineHeight: "30px",
    textTransform: "uppercase",
    border: "none",
    textDecoration: "none"
  }
};

class ColorBox extends Component {
  constructor(props) {
    super(props);
    this.state = {
      copied: false
    };
    this.changeCopyState = this.changeCopyState.bind(this);
  }

  changeCopyState() {
    this.setState({ copied: true }, () => {
      setTimeout(() => this.setState({ copied: false }), 1500);
    });
  }

  render() {
    const { name, background, moreUrl, showLink, classes } = this.props;
    const { copied } = this.state;
    const isLightColor = chroma(background).luminance() >= 0.65;
    const btnClassStr = isLightColor ? "copy-button dark-text" : "copy-button";

    return (
      <CopyToClipboard
        text={ background }
        onCopy={ this.changeCopyState }
      >
        <div
          style={{ background: background }}
          className="ColorBox"
        >
          <div
            style={{ background }}
            className={ `copy-overlay ${ copied && "show" }`}
          />
          <div className={ `copy-msg ${ copied && "show"}`}>
            <h1>copied!</h1>
            <p className={ classes.copyText }>
              { this.props.background }
            </p>
          </div>
          <div className="copy-container">
            <div className="box-content">
              <span className={ classes.colorName }>
                { name }
                { chroma(background).luminance() }
              </span>
            </div>
            <button className={ classes.copyButton }>
              Copy
            </button>
          </div>
          { showLink && (
            <Link
              to={ moreUrl }
              onClick={ evt => evt.stopPropagation() }
            >
              <span className={ classes.seeMore }>
                More
              </span>
            </Link>
          )}
        </div>
      </CopyToClipboard>
    );
  }
}

export default withStyles(styles)(ColorBox);
