import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { CopyToClipboard } from 'react-copy-to-clipboard';
import chroma from 'chroma-js';
import { withStyles } from '@material-ui/styles';
import './ColorBox.css';

const styles = {
  colorBox: {
    height: props => props.showingFullPalette ? "25%" : "50%",
    width: "20%",
    margin: "0 auto",
    display: "inline-block",
    position: "relative",
    cursor: "pointer",
    marginBottom: "-3.5px",
    "&:hover button": {
      transition: "0.3s ease-in-out",
      opacity: 1
    }
  },
  copyText: {
    color: props => chroma(props.background).luminance() >= 0.68 ? "black" : "white"
  },
  colorName: {
    color: props => chroma(props.background).luminance() <= 0.19 ? "white" : "rgba(0,0,0,0.5)"
  },
  seeMore: {
    color: props => chroma(props.background).luminance() >= 0.68 ? "rgba(0,0,0,0.5)" : "white",
    background: "rgba(255, 255, 255, 0.3)",
    position: "absolute",
    border: "none",
    right: "0px",
    bottom: "0px",
    width: "60px",
    height: "30px",
    textAlign: "center",
    lineHeight: "30px",
    textTransform: "uppercase"
  },
  copyButton: {
    color: props => chroma(props.background).luminance() >= 0.68 ? "rgba(0,0,0,0.5)" : "white",
    width: "100px",
    height: "30px",
    position: "absolute",
    display: "inline-block",
    top: "50%",
    left: "50%",
    marginLeft: "-50px",
    marginTop: "-15px",
    textAlign: "center",
    outline: "none",
    background: "rgba(255, 255, 255, 0.3)",
    fontSize: "1rem",
    lineHeight: "30px",
    textTransform: "uppercase",
    border: "none",
    textDecoration: "none",
    opacity: 0
  },
  boxContent: {
    position: "absolute",
    padding: "10px",
    width: "100%",
    left: "0px",
    bottom: "0px",
    color: "black",
    letterSpacing: "1px",
    textTransform: "uppercase",
    fontSize: "12px"
  },
  copyOverlay: {
    opacity: "0",
    zIndex: "0",
    width: "100%",
    height: "100%",
    transition: "transform 0.6s ease-in-out",
    transform: "scale(0.1)"
  },
  showOverlay: {
    opacity: "1",
    transform: "scale(50)",
    zIndex: "10",
    position: "absolute"
  }
};

class ColorBox extends Component {
  constructor(props) {
    super(props);
    this.state = {
      copied: false
    };
    this.changeCopyState = this.changeCopyState.bind(this);
  }

  changeCopyState() {
    this.setState({ copied: true }, () => {
      setTimeout(() => this.setState({ copied: false }), 1500);
    });
  }

  render() {
    const { name, background, moreUrl, showingFullPalette, classes } = this.props;
    const { copied } = this.state;

    return (
      <CopyToClipboard
        text={ background }
        onCopy={ this.changeCopyState }
      >
        <div
          style={{ background: background }}
          className={ classes.colorBox }
        >
          <div
            style={{ background }}
            className={ `${ classes.copyOverlay } ${ copied && classes.showOverlay }`}
          />
          <div className={ `copy-msg ${ copied && classes.showOverlay }` }>
            <h1>copied!</h1>
            <p className={ classes.copyText }>
              { this.props.background }
            </p>
          </div>
          <div>
            <div className={ classes.boxContent }>
              <span className={ classes.colorName }>
                { name }
              </span>
            </div>
            <button className={ classes.copyButton }>
              Copy
            </button>
          </div>
          { showingFullPalette && (
            <Link
              to={ moreUrl }
              onClick={ evt => evt.stopPropagation() }
            >
              <span className={ classes.seeMore }>
                More
              </span>
            </Link>
          )}
        </div>
      </CopyToClipboard>
    );
  }
}

export default withStyles(styles)(ColorBox);
