import sizes from './sizes';

const styles = {
  root: {
    width: "20%",
    height: "25%",
    margin: "0 auto",
    display: "inline-block",
    position: "relative",
    cursor: "pointer",
    marginBottom: "-3.5px",
    overflow: "hidden",
    "&:hover svg": {
      color: "white",
      transform: "scale(1.5)"
    },
    [sizes.down("lg")]: {
      width: "25%",
      height: "20%"
    },
    [sizes.down("md")]: {
      width: "50%",
      height: "10%"
    },
    [sizes.down("sm")]: {
      width: "100%",
      height: "5%"
    }
  },
  boxContent: {
    position: "absolute",
    width: "95%",
    left: "0px",
    bottom: "0px",
    color: props => 
      chroma(props.background).luminance() <= 0.19
        ? "rgba(255,255,255,0.8)"
        : "rgba(0,0,0,0.5)",
    letterSpacing: "1px",
    textTransform: "uppercase",
    padding: "10px",
    marginRight: "10px",
    fontSize: "12px",
    display: "flex",
    justifyContent: "space-between"
  },
  deleteIcon: {
    transition: "all 0.3s ease-in-out"
  }
};

export default styles;
