import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { CopyToClipboard } from 'react-copy-to-clipboard';
import chroma from 'chroma-js';
import './ColorBox.css';

class ColorBox extends Component {
  constructor(props) {
    super(props);
    this.state = {
      copied: false
    };
    this.changeCopyState = this.changeCopyState.bind(this);
  }

  changeCopyState() {
    this.setState({ copied: true }, () => {
      setTimeout(() => this.setState({ copied: false }), 1500);
    });
  }

  render() {
    const { name, background, moreUrl, showLink } = this.props;
    const { copied } = this.state;
    const isDarkColor = chroma(background).luminance() <= 0.09;
    const isLightColor = chroma(background).luminance() >= 0.75;
    const classStr = isLightColor ? "see-more dark-text" : "see-more";

    return (
      <CopyToClipboard
        text={ background }
        onCopy={ this.changeCopyState }
      >
        <div
          style={{ background: background }}
          className="ColorBox"
        >
          <div
            style={{ background }}
            className={ `copy-overlay ${ copied && "show" }`}
          />
          <div className={ `copy-msg ${ copied && "show"}`}>
            <h1>copied!</h1>
            <p>{ this.props.background }</p>
          </div>
          <div className="copy-container">
            <div className="box-content">
              <span className={ isDarkColor ? "light-text" : "" }>
                { name }
                { chroma(background).luminance() }
              </span>
            </div>
            <button className="copy-button">
              Copy
            </button>
          </div>
          { showLink && (
            <Link
              to={ moreUrl }
              onClick={ evt => evt.stopPropagation() }
            >
              <span className={ classStr }>
                More
              </span>
            </Link>
          )}
        </div>
      </CopyToClipboard>
    );
  }
}

export default ColorBox;
